import { setVictoryLineData, victoryLineData } from "../../Data/list";
import { GetMonthData } from "./MonthDataGenerate";

export function CalculateChartData(){
    //Get month data
    var monthData = GetMonthData()
    console.log(monthData)
    
    //set Variable
    var beforeDay = 0


    monthData.forEach(element => {
        //check if day is the same
        if (beforeDay === element.date.day){

            //refresh old data
            var newDailyBankBalance = []
            var newDailyCost = []
            var newDailyIncomm = []

            // calculate newBankBalance
            /*for (let num = 0; victoryLineData[0][0].length - 1 < num; num++){
                newDailyBankBalance.push(victoryLineData[0][0][num])
            }
            newDailyBankBalance.push(element.bankBalance)*/


            //check if element is cost or incomm
            if (element.state === "Einkommen"){
                // calculate newIncomm
                for (let num = 0; victoryLineData[0][1].length - 1 < num; num++){
                    newDailyIncomm.push(victoryLineData[0][1][num])
                }
                newDailyIncomm.push(parseFloat(victoryLineData[0][1][victoryLineData[0][1].length - 1]) + parseFloat(element.amount))

                // set new Data
                setVictoryLineData([[newDailyBankBalance, newDailyIncomm, victoryLineData[0][2]], victoryLineData[1]])
            }
            else{
                // calculate newCost
                for (let num = 0; victoryLineData[0][2].length - 1 < num; num++){
                    newDailyCost.push(victoryLineData[0][2][num])
                }
                newDailyCost.push(parseFloat(victoryLineData[0][2][victoryLineData[0][2].length - 1]) + parseFloat(element.amount))

                // set new Data
                setVictoryLineData([[newDailyBankBalance, victoryLineData[0][1], newDailyCost.reverse()], victoryLineData[1]])
            }
        
        }
        else{
            // set new data
            victoryLineData[0][0].push(element.bankBalance)

            if (element.state === "Einkommen") victoryLineData[0][1].push(element.amount)
            else victoryLineData[0][2].push(element.amount)
        }

        //set day for next
        beforeDay = element.date.day
    });

    console.log(monthData)
    console.log(victoryLineData);
}
