import { DefaultButton } from "../Buttons/defaultButton"
import { HardDataReset } from "../Functions/DataFunctions/ClearData"
import { GetCurrentChoice } from "../Functions/DataFunctions/Datadealer"
import { centering } from "../Styles/Look"
import { bgColor1f, bgColor2e, bgColor3e, bgColor4e, bgColor5e } from "../Styles/backGroundColor"

export function SettingsModal({open, onClose}, props){
    const {fulleHeight=100, borderRadius=20, buttonHeight=35, fontSize=19} = props
    // check if open
    if (!open) return

    return (
    <div style={{width:"100%", height:fulleHeight, backgroundColor:bgColor2e, borderRadius:borderRadius}}>
        <DefaultButton click={HardDataReset} name={"Daten von '" + GetCurrentChoice() +"' löschen"} borderRadius={10} 
                       height={buttonHeight} fontSize={fontSize} color={bgColor5e} top={10} left={10} borderColor={bgColor5e}
                       add={centering}/>

        {/*<DefaultButton name="close" fontSize="20px" color="red" click={onClose}/>*/}
    </div>)
}
